<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Contacts extends CI_Controller {	public function index()	{		$this->load->view('public/contact');	}	public function save(){		$contact_array = array(			'title'=>$this->input->post('title'),			'content'=>$this->input->post('message'),			'email'=>$this->input->post('email'),			'address'=>$this->input->post('address')		);		if($query = $this->Contact->save($contact_array)){			echo json_encode(array('success' => true, 'message' => lang('items_successful_adding')));		}else {//failure            echo json_encode(array('success' => false, 'message' => lang('items_error_adding_updating')));        }	}	function get_num_unread(){		$num = $this->Contact->get_num_unread();		echo json_encode(array('num_unread' => $num));		}}